function Change(n){if(n==`About`){container.innerHTML=null;let n=document.createElement("about");container.appendChild(n);riot.mount(n,"about")}else if(n==`Portfolio`){container.innerHTML=null;let n=document.createElement("portfolio");container.appendChild(n);riot.mount(n,"portfolio")}else if(n==`Blog`){container.innerHTML=null;let n=document.createElement("blog");container.appendChild(n);riot.mount(n,"blog")}}let container=document.getElementById("content");